// Helper methods
trait InfersLanguageKeyType
{
    private function inferTypeFromKey(string $key): string
    {
        // Simple heuristics based on key prefixes
        return match (true) {
            str_starts_with($key, 'button.') => 'button',
            str_starts_with($key, 'label.') => 'label',
            str_starts_with($key, 'nav.') => 'nav',
            str_starts_with($key, 'footer.') => 'footer',
            str_starts_with($key, 'header.') => 'header',
            str_starts_with($key, 'title.') => 'title',
            str_starts_with($key, 'hero.') => 'component',
            str_starts_with($key, 'angebot.') => 'page',
            str_starts_with($key, 'ambiente.') => 'page',
            str_starts_with($key, 'agb.') => 'page',
            str_starts_with($key, 'buchen.') => 'page',
            str_starts_with($key, 'datenschutz.') => 'page',
            str_starts_with($key, 'girls.') => 'component',
            default => 'custom',
        };
    }
